import './App.css';

function App() {

  let count = 1;

  function plus(){
    count++;
    document.getElementById("counter").innerHTML = count;

    console.log(count);
  }
  function minus (){
    count--;
    document.getElementById("counter").innerHTML = count;
    console.log(count);
  }

  return (
    <div className="App">
      <div id="counter">{count}</div>
      <hr></hr>
      <button id="plus" onClick={plus}>+</button>
      <button id="minus" onClick={minus}>-</button>
    </div>
  );

}

export default App;
