function App() {
  const [data, setData] = useState([
    { seq: 1, writer: "tom", message: "Hello React" },
    { seq: 2, writer: "sara", message: "React State Practice" },
    { seq: 3, writer: "jack", message: "Object Array" },
  ]);
  const [obj, setObj] = useState({ seq: 0, writer: "", message: "" });

  //버튼
  const handelSave = () => {
    setData((prev) => { 
      return [...prev, obj]; 
    });
    setObj({ seq: "", writer: "", message: "" });
  };

  //input
  const handelUser = (e) => {
    setObj((prev) => { //기존 객체를 불러옴
      return { ...prev, writer: e.target.value };
    }); //기존객체, 새 객체
  };
  const handelContents = (e) => {
    setObj((prev) => {
      return { ...prev, message: e.target.value };
    });
  };
  const handelSeq = (e) => {
    setObj((prev) => {
      return { ...prev, seq: e.target.value };
    });
  };

  return (
    <div className="container">
      <table className="messages">
        <tr>
          <th>글번호</th>
          <th>작성자</th>
          <th>메세지</th>
        </tr>

        {data.map((data, index) => {
          return (
            <tr key={index}>
              <td>{data.seq}</td>
              <td>{data.writer}</td>
              <td>{data.message}</td>
            </tr>
          );
        })}
        <tr>
          <td colSpan={3}>
            <input
              placeholder="글번호"
              type="text"
              onChange={handelSeq}
              value={obj.seq || ''}
            />
            <input
              type="text"
              placeholder="작성자"
              onChange={handelUser}
              value={obj.writer}
            />
            <input
              type="text"
              placeholder="내용"
              onChange={handelContents}
              value={obj.message}
            />
            <button onClick={handelSave}>추가</button>
          </td>
        </tr>
      </table>
    </div>
  );
}

export default App;
