let count2 = 10; //함수가 다시 호출될 때 새로 선언됨 
function App() {
  // 상태변수 생성 (상태변수로 UI 가 변경되어야 함) 
  // 한 번 선언되면 다시 선언되지 않음 -> 리렌더링에 의해서는 다시 선언되지 않음 
  //컴포넌트가 unmount 될 때는 초기화 됨 
  const [count, setCounter] = useState(1); 
  const handlePlus = () => { //반드시 setter 를 통해서 값을 변경해야 한다 count++ 이런거 안된다 
    setCounter(count + 1);
    count2++;
  }
  const handleMinus = () => {
    setCounter(count - 1);
    count2--;
  }

  return (
    <div className="App">
      <div>{count2} , {count}</div>
      <hr></hr>
      <button onClick={handlePlus}>+</button>
      <button onClick={handleMinus}>-</button>
    </div>
  );
}

export default App;
