import style from "./Output.module.css";
import { useState } from "react";

const Output = ({ setPage, menus, setMenus }) => {
  // 검색
  const [search, setSearch] = useState("");
  const [filteredMenus, setFilteredMenus] = useState(menus);

  const handleSearch = (e) => {
    const searchText = e.target.value.toLowerCase();
    setSearch(searchText);

    const filteredData = menus.filter((item) => item.name.toLowerCase().includes(searchText));
    setFilteredMenus(filteredData);
  };

  // 삭제
  const handleDelete = (id) => {
    setMenus((prevMenus) => prevMenus.filter((menu) => menu.id !== id));
    setFilteredMenus((prevFilteredMenus) => prevFilteredMenus.filter((menu) => menu.id !== id));
  };

  // 수정
  const [updateItem, setUpdateItem] = useState({ id: "", name: "", price: "" });

  const handleUpdateId = (e) => {
    setUpdateItem((prev) => ({
      ...prev,
      id: e.target.value,
    }));
  };

  const handleUpdateName = (e) => {
    setUpdateItem((prev) => ({
      ...prev,
      name: e.target.value,
    }));
  };

  const handleUpdatePrice = (e) => {
    setUpdateItem((prev) => ({
      ...prev,
      price: e.target.value,
    }));
  };

  const handleUpdate = () => {
    setMenus((prevMenus) =>
      prevMenus.map((menu) => (menu.id === parseInt(updateItem.id) ? updateItem : menu))
    );
    setFilteredMenus((prevFilteredMenus) =>
      prevFilteredMenus.map((menu) => (menu.id === parseInt(updateItem.id) ? updateItem : menu))
    );
    setUpdateItem({ id: "", name: "", price: "" });
  };

  // 추가
  const [newItem, setNewItem] = useState({ id: "", name: "", price: "" });

  const handleNewId = (e) => {
    setNewItem((prev) => ({
      ...prev,
      id: e.target.value,
    }));
  };

  const handleNewName = (e) => {
    setNewItem((prev) => ({
      ...prev,
      name: e.target.value,
    }));
  };

  const handleNewPrice = (e) => {
    setNewItem((prev) => ({
      ...prev,
      price: e.target.value,
    }));
  };

  const handleAdd = () => {
    setMenus((prevMenus) => [...prevMenus, newItem]);
    setNewItem({ id: "", name: "", price: "" });
  };

  return (
    <div className={style.output}>
      <div className={style.title}>Output</div>
      <div className={style.table}>
        <table>
          <thead>
            <tr>
              <th>ID</th>
              <th>Name</th>
              <th>Price</th>
              <th>Action</th>
            </tr>
          </thead>
          <tbody>
            {filteredMenus.map((menu, index) => (
              <tr key={index}>
                <td>{menu.id}</td>
                <td>{menu.name}</td>
                <td>{menu.price}</td>
                <td>
                  <button onClick={() => handleDelete(menu.id)}>Delete</button>
                  <button onClick={() => setUpdateItem(menu)}>Update</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
        <div className={style.form}>
          <h3>Add New Item</h3>
          <input type="text" placeholder="ID" value={newItem.id} onChange={handleNewId} />
          <input type="text" placeholder="Name" value={newItem.name} onChange={handleNewName} />
          <input type="text" placeholder="Price" value={newItem.price} onChange={handleNewPrice} />
          <button onClick={handleAdd}>Add</button>
        </div>
        <div className={style.form}>
          <h3>Update Item</h3>
          <input type="text" placeholder="ID" value={updateItem.id} onChange={handleUpdateId} />
          <input type="text" placeholder="Name" value={updateItem.name} onChange={handleUpdateName} />
          <input type="text" placeholder="Price" value={updateItem.price} onChange={handleUpdatePrice} />
          <button onClick={handleUpdate}>Update</button>
        </div>
        <div className={style.form}>
          <input type="text" placeholder="Search by name" value={search} onChange={handleSearch} />
        </div>
      </div>
      <div className={style.index}>
        <button onClick={() => setPage("/")}>첫 페이지로</button>
      </div>
    </div>
  );
};

export default Output;
