import style from "./Output.module.css";
import { useState } from "react";

const Output = ({ setPage, menus, setMenus }) => {
  // 복사 데이터
  const [datas, setDatas] = useState(menus);

  // 검색 기능
  const searchBox = (e) => {
    const searchText = e.target.value; // 입력한 검색어
    const filteredData = datas.filter((item) => item.name.includes(searchText)); // 복사된 데이터에서 필터링
    setDatas(filteredData);
  };

  // 삭제 기능
  const [delSeq, setDelSeq] = useState(0);

  const handleDel = (e) => {
    setDelSeq(parseInt(e.target.value));
  };

  const delBtn = () => {
    setMenus((prev) => {
      return prev.filter((item) => {
        return item.id !== delSeq;
      });
    });
  };

  // 수정 기능
  const [upDate, setUpdate] = useState({ id: 0, name: '', price: 0 });

  const handleId = (e) => {
    setUpdate((prev) => ({
      ...prev,
      id: e.target.value
    }));
  };

  const handleName = (e) => {
    setUpdate((prev) => ({
      ...prev,
      name: e.target.value
    }));
  };

  const handlePrice = (e) => {
    setUpdate((prev) => ({
      ...prev,
      price: e.target.value
    }));
  };

  const updateBtn = () => {
    setMenus((prev) => {
      return prev.map((item) => {
        return item.id === upDate.id ? upDate : item;
      });
    });
  };

  return (
    <div className={style.output}>
      <div className={style.title}>Output</div>
      <div className={style.table}>
        <table>
          <thead>
            <tr>
              <th>ID</th>
              <th>Name</th>
              <th>Price</th>
            </tr>
          </thead>
          <tbody>
            {datas.map((menu, i) => (
              <tr key={i}>
                <td>{menu.id}</td>
                <td>{menu.name}</td>
                <td>{menu.price}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
      <div className={style.controls}>
        <div>
          <input type="text" placeholder="ID" onChange={handleDel} />
          <button onClick={delBtn}>삭제</button>
        </div>
        <div>
          <input type="text" placeholder="Name" onChange={handleName} />
          <input type="text" placeholder="Price" onChange={handlePrice} />
          <input type="text" placeholder="ID" onChange={handleId} />
          <button onClick={updateBtn}>수정</button>
        </div>
        <div>
          <input type="text" placeholder="Search by name" onChange={searchBox} />
        </div>
      </div>
      <div className={style.index}>
        <button onClick={() => setPage("/")}>첫 페이지로</button>
      </div>
    </div>
  );
};

export default Output;
