import style from "./Output.module.css";
import { useState } from "react";

const Output = ({ setPage, menus, setMenus }) => {
  // 검색
const [search, setSearch] = useState("");
const handleSearch = (e) => {
  const searchText = e.target.value.toLowerCase(); // 검색어를 소문자로 변환하여 대소문자 구분 없이 검색할 수 있도록 함
  const filteredData = menus.filter((item) => item.name.toLowerCase().includes(searchText)); // 원본 데이터에서 이름에 검색어가 포함된 경우 필터링
  setFilteredMenus(filteredData); // 필터링된 데이터로 filteredMenus 상태 업데이트
};

  // 필터링된 메뉴 상태 추가
  const [filteredMenus, setFilteredMenus] = useState(menus);


  //삭제 
  const [delSeq, setDelSeq] = useState(0);
  const handleDel = (e) => {
    setDelSeq(parseInt(e.target.value));
  };
  const delBtn = () => {
    setMenus((prev) => {
      return prev.filter((item) => {
        return item.id !== delSeq;
      });
    });
  };
  //수정
  const [upDate, setUpdate] = useState({id:0,name:'',price:0});
  const handleId = (e) => {
    setUpdate((prev)=>{
      return {...prev, id:e.target.value}
    })
  };
  const handleName = (e) => {
    setUpdate((prev)=>{
      return {...prev, name:e.target.value}
    })
  };
  const handlePrice = (e) => {
    setUpdate((prev)=>{
      return {...prev, price:e.target.value}
    })
  };
  const updateBtn = () => {
    setMenus((prev)=>{
      return prev.map((item)=>{
        return item.id == upDate.id ? upDate : item
      })
    })
  }
  // 추가
  const [newItem, setNewItem] = useState({ id: "", name: "", price: "" });
  const handleNewId = (e) => {
    setNewItem((prev) => {
      return { ...prev, id: e.target.value };
    });
  };
  const handleNewName = (e) => {
    setNewItem((prev) => {
      return { ...prev, name: e.target.value };
    });
  };
  const handleNewPrice = (e) => {
    setNewItem((prev) => {
      return { ...prev, price: e.target.value };
    });
  };
  const addBtn = () => {
    setMenus((prev) => [...prev, newItem]);
  };







  return (
    <div className={style.output}>
      <div className={style.title}>Output</div>
      <div className={style.table}>
        <table>
          <tr>
            <th>ID</th>
            <th>Name</th>
            <th>Price</th>
          </tr>
           {/* //기존 메뉴를 렌더링하는 부분을 filteredMenus로 대체 */}
            {filteredMenus.map((menu, i) => {
              return (
                <tr key={i}>
                  <td>{menu.id}</td>
                  <td>{menu.name}</td>
                  <td>{menu.price}</td>
                </tr>
              );
            })}
          <tr>
            <td colSpan={2}>
              <input type="text" placeholder="ID" onChange={handleDel} />
            </td>
            <td colSpan={1}>
              <button onClick={delBtn}>삭제</button>
            </td>
          </tr>
          <tr>
            <td>
              <input type="text" placeholder="Name" onChange={handleName}></input>
              <input type="text" placeholder="Price" onChange={handlePrice}></input>
            </td>
            <td>
              <input type="text" placeholder="ID" onChange={handleId}></input>
              <button onClick={updateBtn}>수정</button>
            </td>
          </tr>
            <tr>
              <td>
                <input type="text" placeholder="New ID" onChange={handleNewId} />
                <input type="text" placeholder="New Name" onChange={handleNewName} />
                <input type="text" placeholder="New Price" onChange={handleNewPrice} />
              </td>
              <td>
                <button onClick={addBtn}>추가</button>
              </td>
            </tr>
            <tr>
              <td><input type="text" placeholder="Search by name" value={search} onChange={handleSearch}/></td>
            </tr>
        </table>
      </div>
      <div className={style.index}>
        <button
          onClick={() => {
            setPage("/");
          }}
        >
          첫 페이지로
        </button>
      </div>
    </div>
  );
};

export default Output;
