import style from "./Output.module.css";
import { useState } from "react";

const Output = ({ setPage, menus, setMenus }) => {
  // 검색
  const [search, setSearch] = useState("");
  const [filteredMenus, setFilteredMenus] = useState(menus);

  const handleSearch = (e) => {
    const searchText = e.target.value.toLowerCase();
    setSearch(searchText);

    const filteredData = menus.filter((item) => item.name.toLowerCase().includes(searchText));
    setFilteredMenus(filteredData);
  };

  // 삭제
  const [delSeq, setDelSeq] = useState("");
  const handleDel = (e) => {
    setDelSeq(e.target.value);
  };
  const delBtn = () => {
    setMenus((prev) => prev.filter((item) => item.id !== parseInt(delSeq)));
    setDelSeq("");
  };

  // 수정
  const [upDate, setUpDate] = useState({ id: "", name: "", price: "" });
  const handleId = (e) => {
    setUpDate((prev) => ({
      ...prev,
      id: e.target.value,
    }));
  };
  const handleName = (e) => {
    setUpDate((prev) => ({
      ...prev,
      name: e.target.value,
    }));
  };
  const handlePrice = (e) => {
    setUpDate((prev) => ({
      ...prev,
      price: e.target.value,
    }));
  };
  const updateBtn = () => {
    setMenus((prev) =>
      prev.map((item) => (item.id === parseInt(upDate.id) ? upDate : item))
    );
    setUpDate({ id: "", name: "", price: "" });
  };

  // 추가
  const [newItem, setNewItem] = useState({ id: "", name: "", price: "" });
  const handleNewId = (e) => {
    setNewItem((prev) => ({
      ...prev,
      id: e.target.value,
    }));
  };
  const handleNewName = (e) => {
    setNewItem((prev) => ({
      ...prev,
      name: e.target.value,
    }));
  };
  const handleNewPrice = (e) => {
    setNewItem((prev) => ({
      ...prev,
      price: e.target.value,
    }));
  };
  const addBtn = () => {
    setMenus((prev) => [...prev, newItem]);
    setNewItem({ id: "", name: "", price: "" });
  };

  return (
    <div className={style.output}>
      <div className={style.title}>Output</div>
      <div className={style.table}>
        <table>
          <thead>
            <tr>
              <th>ID</th>
              <th>Name</th>
              <th>Price</th>
            </tr>
          </thead>
          <tbody>
            {filteredMenus.map((menu, i) => (
              <tr key={i}>
                <td>{menu.id}</td>
                <td>{menu.name}</td>
                <td>{menu.price}</td>
              </tr>
            ))}
          </tbody>
          <tfoot>
            <tr>
              <td colSpan={2}>
                <input
                  type="text"
                  placeholder="ID"
                  value={delSeq}
                  onChange={handleDel}
                />
              </td>
              <td colSpan={1}>
                <button onClick={delBtn}>삭제</button>
              </td>
            </tr>
            <tr>
              <td>
                <input
                  type="text"
                  placeholder="Name"
                  value={upDate.name}
                  onChange={handleName}
                />
                <input
                  type="text"
                  placeholder="Price"
                  value={upDate.price}
                  onChange={handlePrice}
                />
              </td>
              <td>
                <input
                  type="text"
                  placeholder="ID"
                  value={upDate.id}
                  onChange={handleId}
                />
                <button onClick={updateBtn}>수정</button>
              </td>
            </tr>
            <tr>
              <td>
                <input
                  type="text"
                  placeholder="New ID"
                  value={newItem.id}
                  onChange={handleNewId}
                />
                <input
                  type="text"
                  placeholder="New Name"
                  value={newItem.name}
                  onChange={handleNewName}
                />
                <input
                  type="text"
                  placeholder="New Price"
                  value={newItem.price}
                  onChange={handleNewPrice}
                />
              </td>
              <td>
                <button onClick={addBtn}>추가</button>
              </td>
            </tr>
            <tr>
              <td>
                <input
                  type="text"
                  placeholder="Search by name"
                  value={search}
                  onChange={handleSearch}
                />
              </td>
            </tr>
          </tfoot>
        </table>
      </div>
      <div className={style.index}>
        <button onClick={() => setPage("/")}>첫 페이지로</button>
      </div>
    </div>
  );
};

export default Output;
