import style from "./Output.module.css";
import { useState } from "react";

const Output = ({ setPage, menus, setMenus }) => {
  // 검색
  const [search, setSearch] = useState("");
  const [filteredMenus, setFilteredMenus] = useState(menus); // 필터링된 메뉴 상태 추가

  const handleSearch = (e) => {
    const searchText = e.target.value.toLowerCase(); // 검색어를 소문자로 변환하여 대소문자 구분 없이 처리
    setSearch(searchText); // 검색어 상태 업데이트

    const filteredData = menus.filter((item) => item.name.toLowerCase().includes(searchText)); // 원본 데이터에서 이름에 검색어가 포함된 경우 필터링
    setFilteredMenus(filteredData); // 필터링된 데이터로 filteredMenus 상태 업데이트
  };

  //삭제 
  const [delSeq, setDelSeq] = useState(0);
  const handleDel = (e) => {
    setDelSeq(parseInt(e.target.value));
  };
  const delBtn = () => {
    setMenus((prev) => {
      return prev.filter((item) => item.id !== delSeq);
    });
  };

  //수정
  const [upDate, setUpDate] = useState({ id: 0, name: '', price: 0 });
  const handleId = (e) => {
    setUpDate((prev) => ({
      ...prev,
      id: parseInt(e.target.value) // ID는 숫자로 파싱하여 업데이트
    }));
  };
  const handleName = (e) => {
    setUpDate((prev) => ({
      ...prev,
      name: e.target.value
    }));
  };
  const handlePrice = (e) => {
    setUpDate((prev) => ({
      ...prev,
      price: parseFloat(e.target.value) // 가격은 실수로 파싱하여 업데이트
    }));
  };
  const updateBtn = () => {
    setMenus((prev) => {
      return prev.map((item) =>
        item.id === upDate.id ? upDate : item
      );
    });
  };

  // 추가
  const [newItem, setNewItem] = useState({ id: "", name: "", price: "" });
  const handleNewId = (e) => {
    setNewItem((prev) => ({
      ...prev,
      id: parseInt(e.target.value) // ID는 숫자로 파싱하여 업데이트
    }));
  };
  const handleNewName = (e) => {
    setNewItem((prev) => ({
      ...prev,
      name: e.target.value
    }));
  };
  const handleNewPrice = (e) => {
    setNewItem((prev) => ({
      ...prev,
      price: parseFloat(e.target.value) // 가격은 실수로 파싱하여 업데이트
    }));
  };
  const addBtn = () => {
    setMenus((prev) => [...prev, newItem]);
  };

  return (
    <div className={style.output}>
      <div className={style.title}>Output</div>
      <div className={style.table}>
        <table>
          <thead>
            <tr>
              <th>ID</th>
              <th>Name</th>
              <th>Price</th>
            </tr>
          </thead>
          <tbody>
            {/* 필터링된 메뉴를 렌더링하는 부분을 filteredMenus로 대체 */}
            {filteredMenus.map((menu, i) => (
              <tr key={i}>
                <td>{menu.id}</td>
                <td>{menu.name}</td>
                <td>{menu.price}</td>
              </tr>
            ))}
          </tbody>
          <tfoot>
            <tr>
              <td colSpan={2}>
                <input type="text" placeholder="ID" onChange={handleDel} />
              </td>
              <td colSpan={1}>
                <button onClick={delBtn}>삭제</button>
              </td>
            </tr>
            <tr>
              <td>
                <input type="text" placeholder="Name" onChange={handleName} />
                <input type="text" placeholder="Price" onChange={handlePrice} />
              </td>
              <td>
                <input type="text" placeholder="ID" onChange={handleId} />
                <button onClick={updateBtn}>수정</button>
              </td>
            </tr>
            <tr>
              <td>
                <input type="text" placeholder="New ID" onChange={handleNewId} />
                <input type="text" placeholder="New Name" onChange={handleNewName} />
                <input type="text" placeholder="New Price" onChange={handleNewPrice} />
              </td>
              <td>
                <button onClick={addBtn}>추가</button>
              </td>
            </tr>
            <tr>
              <td>
                <input type="text" placeholder="Search by name" value={search} onChange={handleSearch} />
              </td>
            </tr>
          </tfoot>
        </table>
      </div>
      <div className={style.index}>
        <button onClick={() => setPage("/")}>첫 페이지로</button>
      </div>
    </div>
  );
};

export default Output;
