import style from "./Output.module.css";
import { useState } from "react";

const Output = ({ setPage, menus, setMenus }) => {
    // 검색 기능
    const [search, setSearch] = useState("");
    const handleSearch = (e) => {
      setSearch(e.target.value);
      const searchText = e.target.value.toLowerCase().trim();
      const filteredData = menus.filter(
        (item) => item.name.toLowerCase().includes(searchText)
      );
      setFilteredMenus(filteredData);
    };

    // 필터링된 메뉴 상태 추가
    const [filteredMenus, setFilteredMenus] = useState(menus);

    // 추가 기능
    const [newMenu, setNewMenu] = useState({ id: "", name: "", price: "" });
    const handleNewId = (e) => {
      setNewMenu((prev) => ({ ...prev, id: e.target.value }));
    };
    const handleNewName = (e) => {
      setNewMenu((prev) => ({ ...prev, name: e.target.value }));
    };
    const handleNewPrice = (e) => {
      setNewMenu((prev) => ({ ...prev, price: e.target.value }));
    };
    const addBtn = () => {
      if (newMenu.name !== "" && newMenu.price !== "") {
        setMenus((prev) => [...prev, newMenu]);
        setNewMenu({ id: "", name: "", price: "" });
      }
    };

  //삭제
  const [delSeq, setDelSeq] = useState(0);
  const handleDel = (e) => {
    setDelSeq(parseInt(e.target.value));
  };
  const delBtn = () => {
    setMenus((prev) => {
      return prev.filter((item) => {
        return item.id !== delSeq;
      });
    });
  };
  //수정 
  const [upDate, setUpdate] = useState({id:0,name:'',price:0});
  const handleId = (e) => {
    setUpdate((prev)=>{
      return {...prev, id:e.target.value}
    })
  };
  const handleName = (e) => {
    setUpdate((prev)=>{
      return {...prev, name:e.target.value}
    })
  };
  const handlePrice = (e) => {
    setUpdate((prev)=>{
      return {...prev, price:e.target.value}
    })
  };
  const updateBtn = () => {
    setMenus((prev) => {
      return prev.map((item) => {
        return item.id === upDate.id ? upDate : item;
      });
    });
  };

  return (
    <div className={style.output}>
      <div className={style.title}>Output</div>
      <div className={style.table}>
        <table>
          <tr>
            <th>ID</th>
            <th>Name</th>
            <th>Price</th>
          </tr>
          {menus.map((menu, i) => {
            return (
              <tr key={i}>
                <td>{menu.id}</td>
                <td>{menu.name}</td>
                <td>{menu.price}</td>
              </tr>
            );
          })}
          <tr>
            <td colSpan={2}>
              <input type="text" placeholder="ID" onChange={handleDel} />
            </td>
            <td colSpan={1}>
              <button onClick={delBtn}>삭제</button>
            </td>
          </tr>
          <tr>
            <td>
              <input type="text" placeholder="Name" onChange={handleName}></input>
              <input type="text" placeholder="Price" onChange={handlePrice}></input>
            </td>
            <td>
              <input type="text" placeholder="ID" onChange={handleId}></input>
              <button onClick={updateBtn}>수정</button>
            </td>
          </tr>
        </table>
      </div>
      <div className={style.index}>
        <button
          onClick={() => {
            setPage("/");
          }}
        >
          첫 페이지로
        </button>
      </div>
    </div>
  );
};

export default Output;
