import './App.css';
import { useStore } from './store'; // store 파일로부터 useStore를 가져옴

const Com2 = () => {
  const { str, setStr } = useStore(state => ({
    str: state.str,
    setStr: state.setStr
  })); // useStore 훅을 사용하여 상태 값을 가져옴

  return (
    <div>
      {str}
      <hr/>
      <button onClick={() => setStr("Hello zustand")}>str 내용 변경</button>
    </div>
  );
};

const Com1 = () => {
  return (
    <div>
      <Com2 />
    </div>
  );
};

function App() {
  return (
    <div className="container">
      <Com1 />
    </div>
  );
}

export default App;
