import './App.css';
import { useStore } from './store/st'; // store 파일로부터 useStore를 가져옴



//컴포넌트 (원래는 따로 만n들어야 함)
const Com2 = ()=>{

  const {str, setStr} = useStore(); //js 에 있는 객체를 리턴한 걸 여기로 받음 

  return(
    <div>
      {str}
      <hr/>
      <button onClick={()=>{setStr("Hello zustand")}}>str 내용 변경</button>
    </div>
  )
}
const Com1 = () => {
  return (
    <div>
        <Com2/>
    </div>
  )
}

function App() {

  return (
    <div className="container">
      <Com1 />
    </div>
  );
}

export default App;
